import "./App.css";
import React, { useState } from "react";
import { WhatsappShareButton, TwitterShareButton, FacebookShareButton } from 'react-share';

const App = () => {
  const url = "https://api.quotable.io/random";
  let quoteData = {
    content: "Let time be your only competitor.",
    author: "Ahmed Saber",
  };
  const [quote, setQuote] = useState(quoteData);

  const generateQuote = () => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setQuote(data);
      });
  };

  const copy = () => {
    navigator.clipboard.writeText(
      quote.author + " once said: " + quote.content
    );
    alert("copied");
  };

  return (
    <>
      <h1>Quote Generator React App</h1>
      <div className="container">
        <p>{quote.content}</p>
        <span>{quote.author}</span>

        <div className="btns">
          <button onClick={copy} className="btn">
            Copy
          </button>
          <button onClick={generateQuote}>Generate Another Quote</button>  
        </div>

        <div>
          <h3>share with</h3>

          <div className="share">
          <FacebookShareButton
          url={"https://api.quotable.io"}
          quote={quote.content + " - " + quote.author}>
          <button className="btn face">Facebook</button>
          </FacebookShareButton>

          
        <WhatsappShareButton
          url={"https://api.quotable.io"}
          title={quote.content + " - " + quote.author}>
          <button className="btn whats">WhatsApp</button>
          </WhatsappShareButton>

          <TwitterShareButton
            url={"https://api.quotable.io"}
            title={quote.content + " - " + quote.author}
            hashtags={["quotes", "react"]}>
            <button className="btn tweet">Twitter</button>
          </TwitterShareButton>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
